function ConvertIntToString(n){switch(n){case 1:return"一";case 2:return"两";case 3:return"三";case 4:return"四";case 5:return"五";case 6:return"六";case 7:return"七";case 8:return"八";case 9:return"九"}}function Time(){var n=new Date,t=n.getMonth();return t===12&&n.getDay()>=14?ConvertIntToString(n.getYear()-114)+"年多":t===9&&n.getDay()<14||t<9?ConvertIntToString(n.getYear()-115)+"年多":"近"+ConvertIntToString(n.getYear()-114)+"年"}